/*
 * Nominal entry point for our SPA. This runs
 * on the client. Here we'll just mount the <App/>
 * component and be done. It will do the rest.
 */

'use strict'

import React from 'react'
import App from 'lib/views/app.jsx'

// This is necessary in order for things like generators
// to work client-side. Presumably it increases the size
// of your bundle so babel makes it opt-in.
require('babel/polyfill')

var appEl = document.querySelector('#app')

React.render(<App/>, appEl)
