/*
 * Nominal entry point for our SPA. This runs
 * on the client. We'll just mount the react
 * component and be done. Most of the SPA is
 * hooked into from the App component.
 */

'use strict'

import React from 'react'
import App from 'lib/views/app.jsx'

require('babel/polyfill')

var appEl = document.querySelector('#app')

React.render(<App/>, appEl)
